import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_URL;

const axiosInstance = axios.create({
  baseURL: '/backend/api',
});

const api = {
  getTasks: () => axiosInstance.get('/GetItems.php'), // Adjust the URL as needed
  createTask: (task) => axiosInstance.post('/CreateItem.php', task), // Adjust the URL as needed
  updateTask: (id, task) => axiosInstance.put(`/UpdateItem.php/${id}`, task), // Adjust the URL as needed
  getTask: (id) => axiosInstance.get(`/GetItem.php/${id}`), // Adjust the URL as needed
  deleteTask: (id) => axiosInstance.delete(`//${id}`), // Adjust the URL as needed
};

export default api;
