import axios from 'axios';

const axiosInstance = axios.create({
  baseURL: 'http://localhost:8000', // The root URL of your PHP server
});

const api = {
  getTasks: () => axiosInstance.get('/app/controllers/GetItems.php'), // Adjust the URL as needed
  createTask: (task) => axiosInstance.post('/app/controllers/CreateItem.php', task), // Adjust the URL as needed
  updateTask: (id, task) => axiosInstance.put(`/app/controllers/UpdateItem.php/${id}`, task), // Adjust the URL as needed
  getTask: (id) => axiosInstance.get(`/backend/app/controllers/GetItem.php/${id}`), // Adjust the URL as needed
  deleteTask: (id) => axiosInstance.delete(`/backend/app/controllers/DeleteItem.php/${id}`), // Adjust the URL as needed
};

export default api;
