import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_URL;

const axiosInstance = axios.create({
  baseURL: '/backend/api',
});

const api = {
  getTasks: () => axiosInstance.get('/CreateItem.ph'), // Adjust the URL as needed
  createTask: (task) => axiosInstance.post('/tasks', task), // Adjust the URL as needed
  updateTask: (id, task) => axiosInstance.put(`/tasks/${id}`, task), // Adjust the URL as needed
  getTask: (id) => axiosInstance.get(`/tasks/${id}`), // Adjust the URL as needed
  deleteTask: (id) => axiosInstance.delete(`/tasks/${id}`), // Adjust the URL as needed
};

export default api;
